mapper.select:
SELECT

FROM image_status
WHERE name = #{name} AND tag = #{tag}

mapper.update:
UPDATE image_status
local_create_time = #{localCreateTime}, remote_create_time = #{remoteCreateTime}, need_update = #{needUpdate}, last_checked = #{lastChecked}, image_id = #{imageId}, pulling = #{pulling}, progress = #{progress}, updated_at = datetime('now')
WHERE id = #{id}

mapper.delete:
DELETE
FROM image_status
WHERE name = #{name}
  AND tag = #{tag}
